import React from 'react';
import Board from './components/Board';
import Card from './components/Card';


export default class Dashboard extends React.Component {
  constructor(props){

    super(props)
    // this is the initial state
    this.state = {
        div1: "blueviolet",
        div2: "red",
        div3: "yellow",
        div4: "orange",
        colorOfBox1: "transparent",
        colorOfBox2: "transparent",
        colorOfBox3: "transparent",
        colorOfBox4: "transparent",
    }
}

  changeColorOfBox = (id , color) => {
    if( id == "board-1"){
    this.setState({colorOfBox1: color}) // pink and all color would be replaced by corresonding colors based on the div.
    } else if (id == "board-2"){
      this.setState({colorOfBox2: color})
    } else if (id == "board-3"){
      this.setState({colorOfBox3: color})
    } else if (id == "board-4"){
      this.setState({colorOfBox4: color})
    }
  }

  render(){
    return (
      <div className="App">
          <div className = "test" draggable = "true" id = "card-test" style = {{backgroundColor: "blueviolet"}} color = "blueviolet">
              HI
          </div>
        <main className = "flexbox">
          {/* <div className = "test"> */}


          <Board id = "board-1" className = "board" color = {this.state.colorOfBox1} changeColor = {this.changeColorOfBox}>
            {/* <Card id = "card-1" className = "card" draggable = "true">
              <p>Card one</p>
            </Card> */}
             <p>Card one</p>
          </Board>

          <Board id = "board-2" className = "board" color = {this.state.colorOfBox2} changeColor = {this.changeColorOfBox}>
            {/* <Card id = "card-2" className = "card" draggable = "true">
              <p>Card two</p>
            </Card> */}
              <p>Card two</p>
          </Board>

          <Board id = "board-3" className = "board" color = {this.state.colorOfBox3} changeColor = {this.changeColorOfBox}>
            {/* <Card id = "card-3" className = "card" draggable = "true">
              <p>Card three</p>
            </Card> */}
               <p>Card three</p>
          </Board>

          <Board id = "board-4" className = "board" color = {this.state.colorOfBox4} changeColor = {this.changeColorOfBox}>
            {/* <Card id = "card-4" className = "card" draggable = "true">
              <p>Card four</p>
            </Card> */}
                <p>Card four</p>
          </Board>
          {/* </div> */}


        </main>
      </div>
    );
  }
}
