import React from 'react';
import Board from './components/Board';
// import DropColor from './components/'
import Card from './components/Card';


export default class App extends React.Component {
  constructor(props){

    super(props)
    // this is the initial state
    this.state = {
      color: "red",
      colorOfBox1: "transparent",
      colorOfBox2: "transparent",
      colorOfBox3: "transparent",
      colorOfBox4: "transparent"

    }
}
changeColor= (newcolor ) => {

  this.setState({color: newcolor}) 

}

  changeColorOfBox = (id ) => {
    if( id == "board-1"){
    this.setState({colorOfBox1: this.state.color}) // pink and all color would be replaced by corresonding colors based on the div.
    } else if (id == "board-2"){
      this.setState({colorOfBox2: "red"})
    } else if (id == "board-3"){
      this.setState({colorOfBox3: "yellow"})
    } else if (id == "board-4"){
      this.setState({colorOfBox4: "green"})
    }
  }

  function

  render(){
    return (
      <div className="App">
           <Card id = "color1" className = "card" draggable = "true" color = "green" changeColor = {this.changeColor}> <p>Color 1</p></Card>
           <Card id = "color2" className = "card" draggable = "true" color = "blue" changeColor = {this.changeColor}> <p>Color 2</p></Card>
           <Card id = "color3" className = "card" draggable = "true" color = "purple" changeColor = {this.changeColor}> <p>Color 3</p></Card>
           <Card id = "color4" className = "card" draggable = "true" color = "brown" changeColor = {this.changeColor}> <p>Color 4</p></Card>
        <div className = "flexbox">
        {/* <div className = "test" type="color" draggable = "true"  id = "cardc" style = {{backgroundColor: "blueviolet"}} color = "blueviolet">
              HI
          </div> */}


          {/* <div className = "test" draggable = "true"  id = "card-test2" style = {{backgroundColor: "red"}} color = "red">
              HI
          </div>
          <div className = "test" draggable = "true"  id = "card-test3" style = {{backgroundColor: "blue"}} color = "blue">
              HI
          </div>
          <div className = "test" draggable = "true"  id = "card-test4" style = {{backgroundColor: "green"}} color = "green">
              HI
          </div> */}



          <Board id = "board-1" ondragover="allowDrop(event)"   className = "board" color = {this.state.colorOfBox1} changeColorOfBox = {this.changeColorOfBox}>

             <p>Card one</p>
          </Board>
          <Board id = "board-2" ondragover="allowDrop(event)" className = "board" color = {this.state.colorOfBox2} changeColorOfBox = {this.changeColorOfBox}>

              <p>Card two</p>
          </Board>

          {/* <Board id = "board-2" ondragover="allowDrop(event)" className = "board" color = {this.state.colorOfBox2} changeColor = {this.changeColorOfBox}>

              <p>Card two</p>
          </Board>

          <Board id = "board-3" ondragover="allowDrop(event)" className = "board" color = {this.state.colorOfBox3} changeColor = {this.changeColorOfBox}>

               <p>Card three</p>
          </Board>

          <Board id = "board-4" ondragover="allowDrop(event)" className = "board" color = {this.state.colorOfBox4} changeColor = {this.changeColorOfBox}>

                <p>Card four</p>
          </Board> */}


        </div>
      </div>
    );
  }
}
