import React from 'react';
import Board from './components/Board';

import Card from './components/Card';


export default class App extends React.Component {
  constructor(props){

    super(props)
    // this is the initial state
    this.state = {
      color: "",
      colorOfBox1: "transparent",
      colorOfBox2: "transparent",
      colorOfBox3: "transparent",
      colorOfBox4: "transparent"

    }
  }
  changeColor= (newcolor ) => {
    this.setState({color: newcolor}) 
  }

  changeColorOfBox = (id) => {
    if( id === "Box_1"){
    this.setState({colorOfBox1: this.state.color}) 
    } else if (id === "Box_2"){
      this.setState({colorOfBox2: this.state.color})
    } else if (id === "Box_3"){
      this.setState({colorOfBox3: this.state.color})
    } else if (id === "Box_4"){
      this.setState({colorOfBox4: this.state.color})
    }
  }


  render(){
    return (
      //we do not need "className = "App" for div below(?)"
      <div style = "background-color: red"> 
           <Card id = "color1" className = "card" draggable = "true" color = "yellow" changeColor = {this.changeColor}> <p>Color 1</p></Card>
           <Card id = "color2" className = "card" draggable = "true" color = "red" changeColor = {this.changeColor}> <p>Color 2</p></Card>
           <Card id = "color3" className = "card" draggable = "true" color = "purple" changeColor = {this.changeColor}> <p>Color 3</p></Card>
           <Card id = "color4" className = "card" draggable = "true" color = "aqua" changeColor = {this.changeColor}> <p>Color 4</p></Card>
        <div className = "flexbox">
          <Board id = "Box_1" className = "board" color = {this.state.colorOfBox1} changeColorOfBox = {this.changeColorOfBox}> <p>Box one</p> </Board>
          <Board id = "Box_2" className = "board" color = {this.state.colorOfBox2} changeColorOfBox = {this.changeColorOfBox}> <p>Box two</p> </Board>
          <Board id = "Box_3" className = "board" color = {this.state.colorOfBox3} changeColorOfBox = {this.changeColorOfBox}> <p>Box three</p> </Board>
          <Board id = "Box_4" className = "board" color = {this.state.colorOfBox4} changeColorOfBox = {this.changeColorOfBox}> <p>Box four</p> </Board>


        </div>
      </div>
    );
  }
}
