import React from 'react';
import Board from './components/Board';
import Card from './components/Card';


export default class Dashboard extends React.Component {
  constructor(props){

    super(props)
    // this is the initial state
    this.state = {
        colorOfBox: "aqua",
    }
}

  // changeColor = () => {
  //   if(this.state.colorOfBox === "transparent"){
  //     this.state({
  //       backgroundColor: "aqua",
  //     })
  //   }
  // }

  render(){
    return (
      <div className="App">
        <main className = "flexbox">
          {/* <div className = "test"> */}
          <Board id = "board-1" className = "board" color = "colorOfBox">
            <Card id = "card-1" className = "card" draggable = "true">
              <p>Card one</p>
            </Card>
          </Board>

          <Board id = "board-2" className = "board" color = "yellow">
            <Card id = "card-2" className = "card" draggable = "true">
              <p>Card two</p>
            </Card>
          </Board>

          <Board id = "board-3" className = "board" color = "red">
            <Card id = "card-3" className = "card" draggable = "true">
              <p>Card three</p>
            </Card>
          </Board>

          <Board id = "board-4" className = "board" color = "green">
            <Card id = "card-4" className = "card" draggable = "true">
              <p>Card four</p>
            </Card>
          </Board>
          {/* </div> */}


        </main>
      </div>
    );
  }
}
