import Board from './components/Board';
import Card from './components/Card';


export default class Dashboard extends React.Component {
  constructor(props){

    super(props)
    // this is the initial state
    this.state = {
        colorOfBox: "transparent",

    }
}

  changeColorOfBox = (id) => {
    if( id == "board-2"){
    this.setState({colorOfBox: "pink"})
    }
  }

  render(){
    return (
      <div className="App">
          <div className = "test" draggable = "true" id = "card-test">
              HI
          </div>
        <main className = "flexbox">
          {/* <div className = "test"> */}


          <Board id = "board-1" className = "board" color = {this.state.colorOfBox} changeColor = {this.changeColorOfBox}>
            {/* <Card id = "card-1" className = "card" draggable = "true">
              <p>Card one</p>
            </Card> */}
             <p>Card one</p>
          </Board>

          <Board id = "board-2" className = "board" color = {this.state.colorOfBox} changeColor = {this.changeColorOfBox}>
            {/* <Card id = "card-2" className = "card" draggable = "true">
              <p>Card two</p>
            </Card> */}
              <p>Card two</p>
          </Board>

          <Board id = "board-3" className = "board" color = {this.state.colorOfBox} changeColor = {this.changeColorOfBox}>
            {/* <Card id = "card-3" className = "card" draggable = "true">
              <p>Card three</p>
            </Card> */}
               <p>Card three</p>
          </Board>

          <Board id = "board-4" className = "board" color = {this.state.colorOfBox} changeColor = {this.changeColorOfBox}>
            {/* <Card id = "card-4" className = "card" draggable = "true">
              <p>Card four</p>
            </Card> */}
                <p>Card four</p>
          </Board>
          {/* </div> */}


        </main>
      </div>
    );
  }
}
