import React from 'react'

function Board(props){

    const drop = e => {
        e.preventDefault()
        const card_id = e.dataTransfer.getData('card_id');

        const card = document.getElementById(card_id);
        card.style.display = 'block';

        const card_color = e.dataTransfer.getData('color');
        const color = document.getElementById(card_color);
        card_color.style.backgroundColor= "black";
        // card.style.backgroundColor = "brown";

        e.target.appendChild(card);
    }

    const dragOver = e => {
        e.preventDefault();
        console.log("hello")
    }
    return (
        <div
        id = {props.id}
        className = {props.className}
        style = {{backgroundColor: props.color}}
        // style = {{backgroundColor:props.state.backgroundColor}}

        onDrop = {drop}
        onDragOver = {dragOver}
        >

            {props.children}
        </div>
    )
}
export default Board
